import React, { useState } from 'react';
import { useApp } from '../context/AppContext';
import { sectors, investmentStyles, swipeCards } from '../data/mockData';
import { User } from '../types';

const OnboardingScreen: React.FC = () => {
  const { state, dispatch } = useApp();
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    selectedSectors: [] as string[],
    selectedStyles: [] as string[],
    riskTolerance: 'moderate' as 'conservative' | 'moderate' | 'aggressive'
  });
  const [currentCardIndex, setCurrentCardIndex] = useState(0);

  const handleNext = () => {
    if (state.onboardingStep < 3) {
      dispatch({ type: 'SET_ONBOARDING_STEP', payload: state.onboardingStep + 1 });
    } else {
      // Complete onboarding
      const user: User = {
        id: '1',
        name: formData.name,
        age: parseInt(formData.age),
        sectors: formData.selectedSectors,
        riskTolerance: formData.riskTolerance,
        investmentStyle: formData.selectedStyles,
        portfolio: [],
        watchlist: [],
        preferences: {
          notifications: true,
          darkMode: false,
          emailUpdates: true
        }
      };
      dispatch({ type: 'SET_USER', payload: user });
      dispatch({ type: 'SET_SCREEN', payload: 'home' });
    }
  };

  const handleBack = () => {
    if (state.onboardingStep > 0) {
      dispatch({ type: 'SET_ONBOARDING_STEP', payload: state.onboardingStep - 1 });
    }
  };

  const handleSwipe = (direction: 'left' | 'right') => {
    const currentCard = swipeCards[currentCardIndex];
    if (direction === 'right') {
      if (currentCard.type === 'sector') {
        setFormData(prev => ({
          ...prev,
          selectedSectors: [...prev.selectedSectors, currentCard.title]
        }));
      } else if (currentCard.type === 'style') {
        setFormData(prev => ({
          ...prev,
          selectedStyles: [...prev.selectedStyles, currentCard.title]
        }));
      }
    }
    
    if (currentCardIndex < swipeCards.length - 1) {
      setCurrentCardIndex(currentCardIndex + 1);
    } else {
      handleNext();
    }
  };

  const renderStep = () => {
    switch (state.onboardingStep) {
      case 0:
        return (
          <div className="p-6 max-w-md mx-auto">
            <h2 className="text-2xl font-bold mb-6 text-center">What's your name?</h2>
            <div className="mb-6">
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                placeholder="Enter your name"
                className="w-full p-4 border border-gray-300 rounded-xl text-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
            </div>
            <button
              onClick={handleNext}
              disabled={!formData.name.trim()}
              className="btn-primary w-full disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Continue
            </button>
          </div>
        );

      case 1:
        return (
          <div className="p-6 max-w-md mx-auto">
            <h2 className="text-2xl font-bold mb-6 text-center">How old are you?</h2>
            <div className="mb-6">
              <input
                type="number"
                value={formData.age}
                onChange={(e) => setFormData(prev => ({ ...prev, age: e.target.value }))}
                placeholder="Enter your age"
                min="18"
                className="w-full p-4 border border-gray-300 rounded-xl text-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
            </div>
            <div className="flex gap-4">
              <button onClick={handleBack} className="btn-outline flex-1">
                Back
              </button>
              <button
                onClick={handleNext}
                disabled={!formData.age || parseInt(formData.age) < 18}
                className="btn-primary flex-1 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Continue
              </button>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="p-6">
            <h2 className="text-2xl font-bold mb-2 text-center">Tell us what interests you</h2>
            <p className="text-gray-600 text-center mb-8">We'll use this to find your perfect investment matches</p>
            
            <div className="mb-8">
              <h3 className="text-lg font-semibold mb-4">Sectors</h3>
              <div className="grid grid-cols-2 gap-4">
                {sectors.map((sector) => (
                  <button
                    key={sector.name}
                    onClick={() => {
                      setFormData(prev => ({
                        ...prev,
                        selectedSectors: prev.selectedSectors.includes(sector.name)
                          ? prev.selectedSectors.filter(s => s !== sector.name)
                          : [...prev.selectedSectors, sector.name]
                      }));
                    }}
                    className={`p-4 rounded-xl border-2 transition-all ${
                      formData.selectedSectors.includes(sector.name)
                        ? 'border-primary-500 bg-primary-50'
                        : 'border-gray-200 hover:border-primary-300'
                    }`}
                  >
                    <div className="text-2xl mb-2">{sector.icon}</div>
                    <div className="font-medium">{sector.name}</div>
                  </button>
                ))}
              </div>
            </div>

            <div className="mb-8">
              <h3 className="text-lg font-semibold mb-4">Investment Styles</h3>
              <div className="grid grid-cols-2 gap-4">
                {investmentStyles.map((style) => (
                  <button
                    key={style.name}
                    onClick={() => {
                      setFormData(prev => ({
                        ...prev,
                        selectedStyles: prev.selectedStyles.includes(style.name)
                          ? prev.selectedStyles.filter(s => s !== style.name)
                          : [...prev.selectedStyles, style.name]
                      }));
                    }}
                    className={`p-4 rounded-xl border-2 transition-all ${
                      formData.selectedStyles.includes(style.name)
                        ? 'border-primary-500 bg-primary-50'
                        : 'border-gray-200 hover:border-primary-300'
                    }`}
                  >
                    <div className="text-2xl mb-2">{style.icon}</div>
                    <div className="font-medium text-sm">{style.name}</div>
                  </button>
                ))}
              </div>
            </div>

            <div className="flex gap-4">
              <button onClick={handleBack} className="btn-outline flex-1">
                Back
              </button>
              <button
                onClick={handleNext}
                disabled={formData.selectedSectors.length === 0 && formData.selectedStyles.length === 0}
                className="btn-primary flex-1 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Continue
              </button>
            </div>
          </div>
        );

      case 3:
        return (
          <div className="p-6">
            <h2 className="text-2xl font-bold mb-2 text-center">Swipe to refine your preferences</h2>
            <p className="text-gray-600 text-center mb-8">Swipe right if interested, left if not</p>
            
            <div className="relative h-96 mb-8">
              {currentCardIndex < swipeCards.length && (
                <div className="swipe-card">
                  <div className="p-6 h-full flex flex-col justify-center items-center text-center">
                    <div className="text-6xl mb-4">{swipeCards[currentCardIndex].icon}</div>
                    <h3 className="text-2xl font-bold mb-2">{swipeCards[currentCardIndex].title}</h3>
                    <p className="text-gray-600 mb-4">{swipeCards[currentCardIndex].description}</p>
                    <div className="text-sm text-gray-500">
                      Risk Level: {swipeCards[currentCardIndex].riskLevel}
                    </div>
                    {swipeCards[currentCardIndex].companies && (
                      <div className="text-sm text-gray-500 mt-2">
                        Companies: {swipeCards[currentCardIndex].companies?.join(', ')}
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>

            <div className="flex justify-center gap-8 mb-6">
              <button
                onClick={() => handleSwipe('left')}
                className="w-16 h-16 rounded-full bg-red-500 text-white text-2xl flex items-center justify-center hover:bg-red-600 transition-colors"
              >
                👎
              </button>
              <button
                onClick={() => handleSwipe('right')}
                className="w-16 h-16 rounded-full bg-green-500 text-white text-2xl flex items-center justify-center hover:bg-green-600 transition-colors"
              >
                👍
              </button>
            </div>

            <div className="text-center">
              <button onClick={handleNext} className="text-gray-500 hover:text-gray-700">
                Skip
              </button>
            </div>

            <div className="flex justify-center mt-4">
              <div className="flex gap-2">
                {swipeCards.map((_, index) => (
                  <div
                    key={index}
                    className={`w-2 h-2 rounded-full ${
                      index === currentCardIndex ? 'bg-primary-500' : 'bg-gray-300'
                    }`}
                  />
                ))}
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="pt-12 pb-6">
        {renderStep()}
      </div>
    </div>
  );
};

export default OnboardingScreen;
