import React from 'react';
import { useApp } from '../context/AppContext';
import { TrendingUp, TrendingDown, ArrowRight, MessageCircle, Eye } from 'lucide-react';

const HomeScreen: React.FC = () => {
  const { state, dispatch } = useApp();
  const user = state.user;

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const handleStockClick = (stock: any) => {
    dispatch({ type: 'SET_CURRENT_STOCK', payload: stock });
    dispatch({ type: 'SET_SCREEN', payload: 'stock-detail' });
  };

  const topMatches = state.stocks
    .filter(stock => stock.matchPercentage > 80)
    .sort((a, b) => b.matchPercentage - a.matchPercentage)
    .slice(0, 3);

  const recentNews = state.news.slice(0, 3);
  const trendingDiscussions = state.forumPosts.slice(0, 2);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header Section */}
      <div className="bg-white border-b border-gray-200 px-6 py-4">
        <h1 className="text-2xl font-bold text-gray-900">
          {getGreeting()}, {user?.name || 'Investor'}!
        </h1>
        {user?.portfolio && user.portfolio.length > 0 && (
          <div className="mt-2">
            <div className="text-sm text-gray-600">Portfolio Value</div>
            <div className="text-xl font-semibold text-gray-900">
              ${user.portfolio.reduce((sum, item) => sum + item.currentValue, 0).toLocaleString()}
            </div>
          </div>
        )}
      </div>

      <div className="p-6 space-y-6">
        {/* Top Matches Section */}
        <section>
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold text-gray-900">Your Top Investment Matches</h2>
            <button className="text-primary-500 text-sm font-medium">See All</button>
          </div>
          
          <div className="space-y-4">
            {topMatches.map((stock) => (
              <div
                key={stock.symbol}
                onClick={() => handleStockClick(stock)}
                className="card-hover cursor-pointer"
              >
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-2">
                      <h3 className="font-bold text-lg">{stock.symbol}</h3>
                      <span className="match-percentage">{stock.matchPercentage}%</span>
                    </div>
                    <p className="text-gray-600 text-sm mb-2">{stock.name}</p>
                    <div className="flex items-center gap-4">
                      <span className="text-lg font-semibold">${stock.price}</span>
                      <div className={`flex items-center gap-1 ${
                        stock.changePercent > 0 ? 'positive-change' : 
                        stock.changePercent < 0 ? 'negative-change' : 'neutral-change'
                      }`}>
                        {stock.changePercent > 0 ? <TrendingUp size={16} /> : <TrendingDown size={16} />}
                        <span className="text-sm font-medium">
                          {stock.changePercent > 0 ? '+' : ''}{stock.changePercent.toFixed(2)}%
                        </span>
                      </div>
                    </div>
                  </div>
                  <div className="w-20 h-12 bg-gray-100 rounded flex items-center justify-center">
                    <span className="text-xs text-gray-500">Chart</span>
                  </div>
                </div>
                <div className="mt-3 pt-3 border-t border-gray-100">
                  <p className="text-sm text-gray-600">
                    {stock.matchReasons.slice(0, 2).join(' â€¢ ')}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* Quick News Section */}
        <section>
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold text-gray-900">Market Headlines</h2>
            <button 
              onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'news' })}
              className="text-primary-500 text-sm font-medium flex items-center gap-1"
            >
              See All <ArrowRight size={16} />
            </button>
          </div>
          
          <div className="space-y-4">
            {recentNews.map((news) => (
              <div key={news.id} className="card">
                <div className="flex justify-between items-start mb-2">
                  <h3 className="font-semibold text-gray-900 line-clamp-2">{news.title}</h3>
                  <div className={`px-2 py-1 rounded text-xs font-medium ${
                    news.sentiment === 'positive' ? 'bg-green-100 text-green-800' :
                    news.sentiment === 'negative' ? 'bg-red-100 text-red-800' :
                    'bg-gray-100 text-gray-800'
                  }`}>
                    {news.sentiment}
                  </div>
                </div>
                <p className="text-gray-600 text-sm mb-3 line-clamp-2">{news.summary}</p>
                <div className="flex justify-between items-center text-xs text-gray-500">
                  <span>{news.source} â€¢ {new Date(news.timestamp).toLocaleDateString()}</span>
                  {news.relatedStocks.length > 0 && (
                    <span className="text-primary-600">
                      {news.relatedStocks.join(', ')}
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* Trending Discussions */}
        <section>
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold text-gray-900">Hot Topics in Your Niches</h2>
            <button 
              onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'forum' })}
              className="text-primary-500 text-sm font-medium flex items-center gap-1"
            >
              See All <ArrowRight size={16} />
            </button>
          </div>
          
          <div className="space-y-4">
            {trendingDiscussions.map((post) => (
              <div key={post.id} className="card">
                <div className="flex items-start gap-3">
                  <div className="w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
                    <span className="text-primary-600 font-medium text-sm">
                      {post.author.name.charAt(0)}
                    </span>
                  </div>
                  <div className="flex-1">
                    <h3 className="font-semibold text-gray-900 mb-1">{post.title}</h3>
                    <p className="text-gray-600 text-sm mb-3 line-clamp-2">{post.content}</p>
                    <div className="flex items-center justify-between text-xs text-gray-500">
                      <div className="flex items-center gap-4">
                        <span className="flex items-center gap-1">
                          <MessageCircle size={14} />
                          {post.upvotes - post.downvotes}
                        </span>
                        <span>{post.niche}</span>
                      </div>
                      <span>{new Date(post.timestamp).toLocaleDateString()}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* Recommended For You */}
        <section>
          <h2 className="text-xl font-bold text-gray-900 mb-4">Discover More</h2>
          <div className="grid grid-cols-2 gap-4">
            <div className="card text-center">
              <div className="text-3xl mb-2">ðŸ“Š</div>
              <h3 className="font-semibold mb-1">Portfolio Analysis</h3>
              <p className="text-sm text-gray-600 mb-3">Get insights on your investments</p>
              <button 
                onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'portfolio' })}
                className="btn-outline w-full text-sm"
              >
                View Portfolio
              </button>
            </div>
            <div className="card text-center">
              <div className="text-3xl mb-2">ðŸŽ¯</div>
              <h3 className="font-semibold mb-1">Smart Alerts</h3>
              <p className="text-sm text-gray-600 mb-3">Never miss important updates</p>
              <button className="btn-outline w-full text-sm">
                Set Alerts
              </button>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default HomeScreen;
