import React, { useState } from 'react';
import { useApp } from '../context/AppContext';
import { Search, Plus, MessageCircle, ThumbsUp, ThumbsDown } from 'lucide-react';

const ForumScreen: React.FC = () => {
  const { state } = useApp();
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedNiche, setSelectedNiche] = useState('all');

  const niches = ['all', 'Technology', 'Healthcare', 'Finance', 'Energy', 'Real Estate', 'Consumer Goods', 'Transportation', 'Entertainment', 'Utilities', 'Materials'];

  const filteredPosts = state.forumPosts.filter(post => {
    const matchesSearch = post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         post.content.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesNiche = selectedNiche === 'all' || post.niche === selectedNiche;
    return matchesSearch && matchesNiche;
  });

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 px-6 py-4">
        <h1 className="text-2xl font-bold text-gray-900 mb-4">Community Forum</h1>
        
        {/* Search Bar */}
        <div className="relative mb-4">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Search posts and discussions..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-primary-500"
          />
        </div>

        {/* Niche Categories */}
        <div className="flex gap-2 overflow-x-auto pb-2">
          {niches.map((niche) => (
            <button
              key={niche}
              onClick={() => setSelectedNiche(niche)}
              className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap ${
                selectedNiche === niche
                  ? 'bg-primary-500 text-white'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              {niche === 'all' ? 'All Niches' : niche}
            </button>
          ))}
        </div>
      </div>

      {/* New Post Button */}
      <div className="p-6">
        <button className="w-full btn-primary flex items-center justify-center gap-2">
          <Plus size={20} />
          New Post
        </button>
      </div>

      {/* Posts Feed */}
      <div className="px-6 space-y-4">
        {filteredPosts.map((post) => (
          <div key={post.id} className="card">
            <div className="flex items-start gap-3">
              <div className="w-10 h-10 bg-primary-100 rounded-full flex items-center justify-center">
                <span className="text-primary-600 font-medium">
                  {post.author.name.charAt(0)}
                </span>
              </div>
              
              <div className="flex-1">
                <div className="flex items-center gap-2 mb-2">
                  <span className="font-semibold text-gray-900">{post.author.name}</span>
                  <span className="text-xs text-gray-500">â€¢</span>
                  <span className="text-xs text-gray-500">{new Date(post.timestamp).toLocaleDateString()}</span>
                  <span className="text-xs text-gray-500">â€¢</span>
                  <span className="text-xs bg-primary-100 text-primary-600 px-2 py-1 rounded">
                    {post.niche}
                  </span>
                </div>
                
                <h3 className="font-bold text-lg text-gray-900 mb-2">{post.title}</h3>
                <p className="text-gray-600 mb-4 line-clamp-3">{post.content}</p>
                
                {post.relatedStocks.length > 0 && (
                  <div className="mb-4">
                    <div className="text-xs text-gray-500 mb-1">Related Stocks:</div>
                    <div className="flex gap-2">
                      {post.relatedStocks.map((stock) => (
                        <span key={stock} className="text-xs bg-gray-100 text-gray-700 px-2 py-1 rounded">
                          {stock}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
                
                <div className="flex items-center gap-6">
                  <button className="flex items-center gap-2 text-gray-500 hover:text-primary-500 transition-colors">
                    <ThumbsUp size={16} />
                    <span className="text-sm">{post.upvotes}</span>
                  </button>
                  <button className="flex items-center gap-2 text-gray-500 hover:text-red-500 transition-colors">
                    <ThumbsDown size={16} />
                    <span className="text-sm">{post.downvotes}</span>
                  </button>
                  <button className="flex items-center gap-2 text-gray-500 hover:text-primary-500 transition-colors">
                    <MessageCircle size={16} />
                    <span className="text-sm">{post.comments.length}</span>
                  </button>
                  <button className="text-sm text-primary-500 hover:text-primary-600 font-medium">
                    Reply
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
        
        {filteredPosts.length === 0 && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">ðŸ’¬</div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">No posts found</h3>
            <p className="text-gray-600">Try adjusting your search or niche filter</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ForumScreen;
