import React, { useState, useEffect } from 'react';
import { useApp } from '../context/AppContext';
import { stocks, news, forumPosts } from '../data/mockData';
import { TrendingUp, TrendingDown, MessageCircle, Star, Plus, Eye } from 'lucide-react';

const HomeScreen: React.FC = () => {
  const { state, dispatch } = useApp();
  const [isVisible, setIsVisible] = useState(false);
  const [selectedSentiment, setSelectedSentiment] = useState<string | null>(null);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const getMatchColor = (percentage: number) => {
    if (percentage >= 80) return 'text-green-400';
    if (percentage >= 60) return 'text-yellow-400';
    return 'text-orange-400';
  };

  const getMatchGlow = (percentage: number) => {
    if (percentage >= 80) return 'neon-glow-green';
    if (percentage >= 60) return 'neon-glow-orange';
    return 'neon-glow-orange';
  };

  const topMatches = stocks.slice(0, 3).map(stock => ({
    ...stock,
    matchPercentage: Math.floor(Math.random() * 40) + 60 // 60-100%
  }));

  const handleAddToWatchlist = (stock: any) => {
    dispatch({ type: 'ADD_TO_WATCHLIST', payload: stock });
  };

  const handleViewStock = (stock: any) => {
    dispatch({ type: 'SET_SELECTED_STOCK', payload: stock });
    dispatch({ type: 'SET_SCREEN', payload: 'stock-detail' });
  };

  const handleViewForum = () => {
    dispatch({ type: 'SET_SCREEN', payload: 'forum' });
  };

  return (
    <div className={`min-h-screen bg-gradient-to-br from-dark-950 via-dark-900 to-dark-800 transition-all duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      <div className="p-6 pb-24">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold gradient-text mb-2">
            {getGreeting()}, {state.user?.name || 'Investor'}!
          </h1>
          <p className="text-gray-300">Here's what's happening in your investment universe</p>
        </div>

        {/* Top Matches Section */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-white">Top Matches</h2>
            <div className="text-sm text-gray-400">Based on your preferences</div>
          </div>
          
          <div className="space-y-4">
            {topMatches.map((stock, index) => (
              <div 
                key={stock.symbol}
                className={`card-glow p-6 animate-slide-up`}
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <div className="relative">
                      <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl flex items-center justify-center text-2xl font-bold text-white">
                        {stock.symbol.slice(0, 2)}
                      </div>
                      <div className={`absolute -top-2 -right-2 w-8 h-8 rounded-full bg-gradient-to-r from-green-500 to-blue-600 flex items-center justify-center text-xs font-bold text-white ${getMatchGlow(stock.matchPercentage)}`}>
                        {stock.matchPercentage}%
                      </div>
                    </div>
                    
                    <div>
                      <h3 className="text-xl font-bold text-white">{stock.name}</h3>
                      <p className="text-gray-400">{stock.symbol}</p>
                      <div className="flex items-center space-x-2 mt-1">
                        <span className={`text-lg font-bold ${stock.priceChange >= 0 ? 'positive-change' : 'negative-change'}`}>
                          ${stock.price.toFixed(2)}
                        </span>
                        <div className={`flex items-center text-sm ${stock.priceChange >= 0 ? 'positive-change' : 'negative-change'}`}>
                          {stock.priceChange >= 0 ? <TrendingUp size={14} /> : <TrendingDown size={14} />}
                          <span className="ml-1">{Math.abs(stock.priceChange).toFixed(2)}%</span>
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleAddToWatchlist(stock)}
                      className="p-2 bg-white/10 hover:bg-white/20 rounded-lg transition-colors duration-200"
                    >
                      <Plus size={20} className="text-blue-400" />
                    </button>
                    <button
                      onClick={() => handleViewStock(stock)}
                      className="p-2 bg-white/10 hover:bg-white/20 rounded-lg transition-colors duration-200"
                    >
                      <Eye size={20} className="text-purple-400" />
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Market Sentiment */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-white mb-6">Market Sentiment</h2>
          <div className="grid grid-cols-3 gap-4">
            <div className="card text-center p-4">
              <div className="text-2xl mb-2">ðŸ“ˆ</div>
              <div className="text-2xl font-bold gradient-text-green">68%</div>
              <div className="text-sm text-gray-400">Bullish</div>
              <div className="progress-bar mt-2">
                <div className="progress-fill" style={{ width: '68%' }}></div>
              </div>
            </div>
            
            <div className="card text-center p-4">
              <div className="text-2xl mb-2">ðŸ“Š</div>
              <div className="text-2xl font-bold text-gray-300">22%</div>
              <div className="text-sm text-gray-400">Neutral</div>
              <div className="progress-bar mt-2">
                <div className="progress-fill bg-gray-500" style={{ width: '22%' }}></div>
              </div>
            </div>
            
            <div className="card text-center p-4">
              <div className="text-2xl mb-2">ðŸ“‰</div>
              <div className="text-2xl font-bold gradient-text-orange">10%</div>
              <div className="text-sm text-gray-400">Bearish</div>
              <div className="progress-bar mt-2">
                <div className="progress-fill bg-orange-500" style={{ width: '10%' }}></div>
              </div>
            </div>
          </div>
        </div>

        {/* Latest News */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-white mb-6">Latest News</h2>
          <div className="space-y-4">
            {news.slice(0, 3).map((article, index) => (
              <div 
                key={article.id}
                className="card-hover p-4 animate-slide-up"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-start space-x-4">
                  <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center text-white font-bold text-sm">
                    {article.source.slice(0, 2).toUpperCase()}
                  </div>
                  <div className="flex-1">
                    <h3 className="font-semibold text-white mb-2 line-clamp-2">{article.title}</h3>
                    <div className="flex items-center justify-between text-sm text-gray-400">
                      <span>{article.source}</span>
                      <span>{article.timestamp}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Community Discussions */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-white">Community Discussions</h2>
            <button
              onClick={handleViewForum}
              className="text-blue-400 hover:text-blue-300 transition-colors"
            >
              View All
            </button>
          </div>
          
          <div className="space-y-4">
            {forumPosts.slice(0, 3).map((post, index) => (
              <div 
                key={post.id}
                className="card-hover p-4 animate-slide-up"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-start space-x-3">
                  <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-pink-600 rounded-full flex items-center justify-center text-white font-bold text-sm">
                    {post.author.slice(0, 1).toUpperCase()}
                  </div>
                  <div className="flex-1">
                    <h3 className="font-semibold text-white mb-1 line-clamp-1">{post.title}</h3>
                    <p className="text-gray-400 text-sm mb-2 line-clamp-2">{post.content}</p>
                    <div className="flex items-center space-x-4 text-xs text-gray-500">
                      <span className="flex items-center space-x-1">
                        <MessageCircle size={12} />
                        <span>{post.comments}</span>
                      </span>
                      <span className="flex items-center space-x-1">
                        <Star size={12} />
                        <span>{post.likes}</span>
                      </span>
                      <span className="badge-primary">{post.niche}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="grid grid-cols-2 gap-4">
          <button
            onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'portfolio' })}
            className="card-glow p-6 text-center hover:scale-105 transition-transform duration-200"
          >
            <div className="text-3xl mb-3">ðŸ’¼</div>
            <h3 className="font-bold text-white mb-2">Portfolio</h3>
            <p className="text-sm text-gray-400">View your investments</p>
          </button>
          
          <button
            onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'news' })}
            className="card-glow p-6 text-center hover:scale-105 transition-transform duration-200"
          >
            <div className="text-3xl mb-3">ðŸ“°</div>
            <h3 className="font-bold text-white mb-2">News</h3>
            <p className="text-sm text-gray-400">Market updates</p>
          </button>
        </div>
      </div>
    </div>
  );
};

export default HomeScreen;
