import React, { useState, useRef, useEffect } from 'react';
import { useApp } from '../context/AppContext';
import { User } from '../types';

const OnboardingScreen: React.FC = () => {
  const { state, dispatch } = useApp();
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [selectedPreferences, setSelectedPreferences] = useState<string[]>([]);
  const [userName, setUserName] = useState('');
  const [userAge, setUserAge] = useState('');
  const [step, setStep] = useState(1); // 1: name/age, 2: preferences
  const cardRef = useRef<HTMLDivElement>(null);

  const mockSwipeCards = [
    { id: 1, title: 'Technology', icon: 'ðŸ’»', description: 'Tech stocks and innovation' },
    { id: 2, title: 'Healthcare', icon: 'ðŸ¥', description: 'Medical and biotech companies' },
    { id: 3, title: 'Finance', icon: 'ðŸ’°', description: 'Banks and financial services' },
    { id: 4, title: 'Energy', icon: 'âš¡', description: 'Oil, gas, and renewable energy' },
    { id: 5, title: 'Consumer', icon: 'ðŸ›’', description: 'Retail and consumer goods' },
    { id: 6, title: 'Real Estate', icon: 'ðŸ¢', description: 'Property and REITs' },
  ];

  const handleCardClick = (preference: string) => {
    setSelectedPreferences(prev => 
      prev.includes(preference) 
        ? prev.filter(p => p !== preference)
        : [...prev, preference]
    );
  };

  const handleNext = () => {
    if (step === 1) {
      if (userName.trim() && userAge.trim()) {
        setStep(2);
      }
    } else {
      if (currentCardIndex < mockSwipeCards.length - 1) {
        setCurrentCardIndex(prev => prev + 1);
      } else {
        // Finish onboarding
        finishOnboarding();
      }
    }
  };

  const handleSkip = () => {
    finishOnboarding();
  };

  const finishOnboarding = () => {
    // Create user with preferences
    const newUser: User = {
      id: '1',
      name: userName || 'User',
      age: parseInt(userAge) || 25,
      sectors: selectedPreferences,
      riskTolerance: 'moderate',
      investmentStyle: selectedPreferences,
      portfolio: [],
      watchlist: [],
      preferences: {
        notifications: true,
        darkMode: true,
        emailUpdates: false
      }
    };

    dispatch({ type: 'SET_USER', payload: newUser });
    dispatch({ type: 'SET_SCREEN', payload: 'home' });
  };

  if (step === 1) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex flex-col">
        <div className="flex-1 flex items-center justify-center p-6">
          <div className="w-full max-w-md">
            <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-8 border border-white/20 shadow-2xl">
              <div className="text-center mb-8">
                <h1 className="text-3xl font-bold text-white mb-4">Welcome to Quantix</h1>
                <p className="text-gray-300">Let's personalize your experience</p>
              </div>

              <div className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">Name</label>
                  <input
                    type="text"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                    placeholder="Enter your name"
                    className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:border-cyan-400 focus:outline-none"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">Age</label>
                  <input
                    type="number"
                    value={userAge}
                    onChange={(e) => setUserAge(e.target.value)}
                    placeholder="Enter your age"
                    className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:border-cyan-400 focus:outline-none"
                  />
                </div>

                <button
                  onClick={handleNext}
                  disabled={!userName.trim() || !userAge.trim()}
                  className="w-full py-3 bg-gradient-to-r from-cyan-400 to-purple-500 text-white rounded-lg font-semibold hover:shadow-lg hover:shadow-cyan-400/25 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Continue
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex flex-col">
      {/* Header */}
      <div className="p-6">
        <div className="flex justify-between items-center">
          <h1 className="text-2xl font-bold text-white">Choose Your Interests</h1>
          <button
            onClick={handleSkip}
            className="text-gray-400 hover:text-white transition-colors"
          >
            Skip
          </button>
        </div>
        <div className="mt-4">
          <div className="w-full bg-gray-700 rounded-full h-2">
            <div 
              className="bg-gradient-to-r from-cyan-400 to-purple-500 h-2 rounded-full transition-all duration-300"
              style={{ width: `${((currentCardIndex + 1) / mockSwipeCards.length) * 100}%` }}
            ></div>
          </div>
        </div>
      </div>

      {/* Card Container */}
      <div className="flex-1 flex items-center justify-center p-6">
        <div className="w-full max-w-md">
          {/* Current Card */}
          <div
            ref={cardRef}
            className="bg-white/10 backdrop-blur-sm rounded-2xl p-8 border border-white/20 shadow-2xl transform transition-all duration-300 hover:scale-105"
          >
            <div className="text-center">
              <div className="text-6xl mb-4">{mockSwipeCards[currentCardIndex].icon}</div>
              <h2 className="text-3xl font-bold text-white mb-4">
                {mockSwipeCards[currentCardIndex].title}
              </h2>
              <p className="text-gray-300 text-lg mb-8">
                {mockSwipeCards[currentCardIndex].description}
              </p>

              <div className="flex gap-4 justify-center">
                <button
                  onClick={() => handleCardClick(mockSwipeCards[currentCardIndex].title)}
                  className={`px-8 py-3 rounded-full font-semibold transition-all duration-300 ${
                    selectedPreferences.includes(mockSwipeCards[currentCardIndex].title)
                      ? 'bg-gradient-to-r from-cyan-400 to-purple-500 text-white shadow-lg shadow-cyan-400/25'
                      : 'bg-white/10 text-white border border-white/20 hover:bg-white/20'
                  }`}
                >
                  {selectedPreferences.includes(mockSwipeCards[currentCardIndex].title) ? 'Selected' : 'Select'}
                </button>
              </div>
            </div>
          </div>

          {/* Navigation */}
          <div className="flex justify-between items-center mt-8">
            <button
              onClick={() => setCurrentCardIndex(prev => Math.max(0, prev - 1))}
              disabled={currentCardIndex === 0}
              className="px-6 py-3 bg-white/10 text-white rounded-full border border-white/20 disabled:opacity-50 disabled:cursor-not-allowed hover:bg-white/20 transition-all duration-300"
            >
              Previous
            </button>

            <span className="text-white">
              {currentCardIndex + 1} of {mockSwipeCards.length}
            </span>

            <button
              onClick={handleNext}
              className="px-6 py-3 bg-gradient-to-r from-cyan-400 to-purple-500 text-white rounded-full font-semibold hover:shadow-lg hover:shadow-cyan-400/25 transition-all duration-300"
            >
              {currentCardIndex === mockSwipeCards.length - 1 ? 'Finish' : 'Next'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OnboardingScreen;
