import React, { useState } from 'react';
import { useApp } from '../context/AppContext';
import { ArrowLeft, TrendingUp, TrendingDown, MessageCircle, Share2, Bell, Plus } from 'lucide-react';
import { generateChartData } from '../data/mockData';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar } from 'recharts';
import { PortfolioItem } from '../types';
import BottomNavigation from '../components/BottomNavigation';

const StockDetailScreen: React.FC = () => {
  const { state, dispatch } = useApp();
  const { currentStock: stock } = state;
  const [selectedTimeframe, setSelectedTimeframe] = useState('1D');
  const [showBuyModal, setShowBuyModal] = useState(false);
  const [buyQuantity, setBuyQuantity] = useState('');
  const [buyPrice, setBuyPrice] = useState('');

  if (!stock) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-gray-600">No stock selected</p>
          <button 
            onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'home' })}
            className="btn-primary mt-4"
          >
            Go Back
          </button>
        </div>
      </div>
    );
  }

  const chartData = generateChartData(30);
  const timeframes = ['1D', '1W', '1M', '3M', '1Y'];

  const handleBack = () => {
    dispatch({ type: 'SET_SCREEN', payload: 'home' });
  };

  const handleAddToWatchlist = () => {
    dispatch({ type: 'ADD_TO_WATCHLIST', payload: stock });
  };

  const handleBuyStock = () => {
    if (!buyQuantity || !buyPrice) return;

    const shares = parseFloat(buyQuantity);
    const price = parseFloat(buyPrice);
    const currentValue = shares * stock.price;
    const totalGain = currentValue - (shares * price);
    const totalGainPercent = price > 0 ? ((stock.price - price) / price) * 100 : 0;

    const portfolioItem: PortfolioItem = {
      symbol: stock.symbol,
      shares: shares,
      averagePrice: price,
      currentValue: currentValue,
      totalGain: totalGain,
      totalGainPercent: totalGainPercent
    };

    dispatch({ type: 'ADD_TO_PORTFOLIO', payload: portfolioItem });
    setShowBuyModal(false);
    setBuyQuantity('');
    setBuyPrice('');
  };

  const isInWatchlist = state.watchlist.some(s => s.symbol === stock.symbol);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-purple-900 text-white">
      {/* Header */}
      <div className="p-6 pt-12">
        <div className="flex items-center gap-4 mb-6">
          <button onClick={handleBack} className="p-2 hover:bg-white/10 rounded-lg transition-colors">
            <span className="text-2xl">‚Üê</span>
          </button>
          <div className="flex-1">
            <h1 className="text-2xl font-bold text-white">{stock.symbol}</h1>
            <p className="text-gray-400">{stock.name}</p>
          </div>
          <div className="flex gap-2">
            <button className="p-2 hover:bg-white/10 rounded-lg transition-colors">
              <span className="text-lg">üì§</span>
            </button>
            <button className="p-2 hover:bg-white/10 rounded-lg transition-colors">
              <span className="text-lg">üîî</span>
            </button>
          </div>
        </div>
        
        <div className="card-hover p-6 mb-6">
          <div className="flex items-center justify-between">
            <div>
              <div className="text-3xl font-bold text-white">${stock.price.toFixed(2)}</div>
              <div className={`flex items-center gap-1 ${
                stock.changePercent > 0 ? 'positive-change' : 
                stock.changePercent < 0 ? 'negative-change' : 'text-gray-400'
              }`}>
                <span className="text-lg">
                  {stock.changePercent > 0 ? '‚Üó' : stock.changePercent < 0 ? '‚Üò' : '‚Üí'}
                </span>
                <span className="font-medium">
                  {stock.changePercent > 0 ? '+' : ''}{stock.change.toFixed(2)} ({stock.changePercent > 0 ? '+' : ''}{stock.changePercent.toFixed(2)}%)
                </span>
              </div>
            </div>
            <div className="text-right">
              <div className="text-sm text-gray-400">Market Cap</div>
              <div className="font-semibold text-white">{stock.marketCap}</div>
            </div>
          </div>
        </div>
      </div>

      <div className="px-6 space-y-6 mb-20">
        {/* Match Analysis */}
        <section className="card-hover p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-bold text-white">Match Analysis</h2>
            <div className="text-4xl font-bold bg-gradient-to-r from-cyan-400 to-blue-500 bg-clip-text text-transparent">
              {stock.matchPercentage}%
            </div>
          </div>
          <div className="mb-4">
            <h3 className="font-semibold mb-3 text-white">Why this matches you:</h3>
            <ul className="space-y-2">
              {stock.matchReasons.map((reason, index) => (
                <li key={index} className="text-sm text-gray-300 flex items-center gap-3">
                  <div className="w-2 h-2 bg-cyan-500 rounded-full"></div>
                  {reason}
                </li>
              ))}
            </ul>
          </div>
        </section>

        {/* Chart Section */}
        <section className="card-hover p-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold text-white">Price Chart</h2>
            <div className="flex gap-2">
              {timeframes.map((timeframe) => (
                <button
                  key={timeframe}
                  onClick={() => setSelectedTimeframe(timeframe)}
                  className={`px-3 py-1 rounded text-sm font-medium transition-all duration-300 ${
                    selectedTimeframe === timeframe
                      ? 'bg-gradient-to-r from-cyan-500 to-blue-600 text-white shadow-lg shadow-cyan-500/25'
                      : 'bg-white/10 text-gray-300 hover:bg-white/20 hover:text-white'
                  }`}
                >
                  {timeframe}
                </button>
              ))}
            </div>
          </div>
          
          <div className="h-64 mb-4 bg-white/5 rounded-lg p-4">
            <div className="w-full h-full flex items-center justify-center text-gray-400">
              <div className="text-center">
                <div className="text-4xl mb-2">üìà</div>
                <p>Chart coming soon</p>
              </div>
            </div>
          </div>
        </section>

        {/* AI Insights */}
        <section className="card-hover p-6">
          <h2 className="text-xl font-bold mb-4 text-white">AI Insights</h2>
          <div className="space-y-4">
            <div className="p-4 bg-blue-500/10 rounded-lg border border-blue-500/20">
              <h3 className="font-semibold text-blue-400 mb-2">Recent Movement Analysis</h3>
              <p className="text-blue-300 text-sm">
                {stock.symbol} has shown strong momentum in the past week, driven by positive earnings expectations and sector rotation into {stock.sector.toLowerCase()} stocks.
              </p>
            </div>
            <div className="p-4 bg-green-500/10 rounded-lg border border-green-500/20">
              <h3 className="font-semibold text-green-400 mb-2">Key Events</h3>
              <ul className="text-green-300 text-sm space-y-1">
                <li>‚Ä¢ Upcoming earnings report on {new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toLocaleDateString()}</li>
                <li>‚Ä¢ New product launch expected next quarter</li>
                <li>‚Ä¢ Analyst upgrades from major firms</li>
              </ul>
            </div>
          </div>
        </section>

        {/* Sentiment Analysis */}
        <section className="card-hover p-6">
          <h2 className="text-xl font-bold mb-4 text-white">Market Sentiment</h2>
          <div className="grid grid-cols-2 gap-4 mb-4">
            <div className="text-center p-4 bg-white/5 rounded-lg border border-white/10">
              <div className="text-2xl font-bold text-green-400">78</div>
              <div className="text-sm text-gray-400">Sentiment Score</div>
            </div>
            <div className="text-center p-4 bg-white/5 rounded-lg border border-white/10">
              <div className="text-2xl font-bold text-blue-400">+12%</div>
              <div className="text-sm text-gray-400">Social Mentions</div>
            </div>
          </div>
          <div className="space-y-2">
            <div className="flex justify-between items-center text-sm">
              <span className="text-gray-300">Positive News</span>
              <span className="text-green-400 font-medium">65%</span>
            </div>
            <div className="flex justify-between items-center text-sm">
              <span className="text-gray-300">Neutral News</span>
              <span className="text-gray-400 font-medium">25%</span>
            </div>
            <div className="flex justify-between items-center text-sm">
              <span className="text-gray-300">Negative News</span>
              <span className="text-red-400 font-medium">10%</span>
            </div>
          </div>
        </section>

        {/* Community Discussions */}
        <section className="card">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-bold">Community Discussions</h2>
            <button className="text-primary-500 text-sm font-medium">Start Discussion</button>
          </div>
          <div className="space-y-4">
            {state.forumPosts
              .filter(post => post.relatedStocks.includes(stock.symbol))
              .slice(0, 2)
              .map((post) => (
                <div key={post.id} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex items-start gap-3">
                    <div className="w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
                      <span className="text-primary-600 font-medium text-sm">
                        {post.author.name.charAt(0)}
                      </span>
                    </div>
                    <div className="flex-1">
                      <h3 className="font-semibold text-gray-900 mb-1">{post.title}</h3>
                      <p className="text-gray-600 text-sm mb-2 line-clamp-2">{post.content}</p>
                      <div className="flex items-center gap-4 text-xs text-gray-500">
                        <span className="flex items-center gap-1">
                          <MessageCircle size={14} />
                          {post.upvotes - post.downvotes}
                        </span>
                        <span>{new Date(post.timestamp).toLocaleDateString()}</span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
          </div>
        </section>

        {/* Action Buttons */}
        <div className="flex gap-4">
          <button
            onClick={handleAddToWatchlist}
            className={`flex-1 py-3 px-4 rounded-xl font-medium transition-all duration-300 flex items-center justify-center gap-2 ${
              isInWatchlist 
                ? 'bg-red-500/20 text-red-400 border border-red-500/30 hover:bg-red-500/30'
                : 'bg-white/10 text-white border border-white/20 hover:bg-white/20'
            }`}
          >
            <span className="text-lg">{isInWatchlist ? '‚úì' : '+'}</span>
            {isInWatchlist ? 'In Watchlist' : 'Add to Watchlist'}
          </button>
          <button 
            onClick={() => setShowBuyModal(true)}
            className="flex-1 py-3 px-4 bg-gradient-to-r from-cyan-500 to-blue-600 hover:from-cyan-600 hover:to-blue-700 text-white rounded-xl font-medium transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-cyan-500/25"
          >
            Buy Stock
          </button>
        </div>
      </div>

      {/* Buy Modal */}
      {showBuyModal && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
          <div className="card-hover p-6 w-full max-w-md">
            <h3 className="text-xl font-bold mb-4 text-white">Buy {stock.symbol}</h3>
            
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">Number of Shares</label>
                <input
                  type="number"
                  value={buyQuantity}
                  onChange={(e) => setBuyQuantity(e.target.value)}
                  placeholder="0"
                  className="w-full p-3 bg-gray-800 border border-gray-600 rounded-lg text-white focus:border-cyan-500 focus:outline-none"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">Price per Share</label>
                <input
                  type="number"
                  value={buyPrice}
                  onChange={(e) => setBuyPrice(e.target.value)}
                  placeholder={stock.price.toString()}
                  step="0.01"
                  className="w-full p-3 bg-gray-800 border border-gray-600 rounded-lg text-white focus:border-cyan-500 focus:outline-none"
                />
              </div>

              {buyQuantity && buyPrice && (
                <div className="p-4 bg-white/5 rounded-lg border border-white/10">
                  <div className="text-sm text-gray-400 mb-2">Order Summary</div>
                  <div className="text-white font-medium">
                    Total: ${(parseFloat(buyQuantity) * parseFloat(buyPrice)).toFixed(2)}
                  </div>
                </div>
              )}
            </div>

            <div className="flex space-x-3 mt-6">
              <button
                onClick={() => setShowBuyModal(false)}
                className="flex-1 py-2 px-4 bg-gray-600 hover:bg-gray-700 text-white rounded-lg transition-colors"
              >
                Cancel
              </button>
              <button
                onClick={handleBuyStock}
                disabled={!buyQuantity || !buyPrice}
                className="flex-1 py-2 px-4 bg-gradient-to-r from-cyan-500 to-blue-600 hover:from-cyan-600 hover:to-blue-700 text-white rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Buy Stock
              </button>
            </div>
          </div>
        </div>
      )}

      <BottomNavigation />
    </div>
  );
};

export default StockDetailScreen;
