import React, { useState } from 'react';
import { useApp } from '../context/AppContext';
import { TrendingUp, TrendingDown, ExternalLink, Share2 } from 'lucide-react';

const NewsScreen: React.FC = () => {
  const { state } = useApp();
  const [selectedCategory, setSelectedCategory] = useState('all');

  const categories = [
    { id: 'all', name: 'All News' },
    { id: 'market', name: 'Market Overview' },
    { id: 'earnings', name: 'Earnings' },
    { id: 'technology', name: 'Technology' },
    { id: 'breaking', name: 'Breaking News' },
    { id: 'analysis', name: 'Analysis & Opinion' }
  ];

  const filteredNews = state.news.filter(news => {
    if (selectedCategory === 'all') return true;
    return news.category.toLowerCase().includes(selectedCategory);
  });

  const marketIndices = [
    { symbol: 'S&P 500', value: '4,783.35', change: '+0.45%', positive: true },
    { symbol: 'NASDAQ', value: '15,003.22', change: '+0.75%', positive: true },
    { symbol: 'DOW', value: '37,592.98', change: '-0.12%', positive: false }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 px-6 py-4">
        <h1 className="text-2xl font-bold text-gray-900 mb-4">Market News</h1>
        
        {/* Market Overview */}
        <div className="grid grid-cols-3 gap-4 mb-4">
          {marketIndices.map((index) => (
            <div key={index.symbol} className="text-center p-3 bg-gray-50 rounded-lg">
              <div className="text-sm text-gray-600 mb-1">{index.symbol}</div>
              <div className="font-semibold text-gray-900">{index.value}</div>
              <div className={`text-sm font-medium ${index.positive ? 'positive-change' : 'negative-change'}`}>
                {index.change}
              </div>
            </div>
          ))}
        </div>

        {/* Categories */}
        <div className="flex gap-2 overflow-x-auto pb-2">
          {categories.map((category) => (
            <button
              key={category.id}
              onClick={() => setSelectedCategory(category.id)}
              className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap ${
                selectedCategory === category.id
                  ? 'bg-primary-500 text-white'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              {category.name}
            </button>
          ))}
        </div>
      </div>

      {/* News Feed */}
      <div className="p-6 space-y-4">
        {filteredNews.map((news) => (
          <div key={news.id} className="card">
            <div className="flex justify-between items-start mb-3">
              <div className="flex-1">
                <h3 className="font-bold text-lg text-gray-900 mb-2 line-clamp-2">{news.title}</h3>
                <p className="text-gray-600 text-sm mb-3 line-clamp-3">{news.summary}</p>
              </div>
              <div className={`ml-4 px-3 py-1 rounded text-xs font-medium ${
                news.sentiment === 'positive' ? 'bg-green-100 text-green-800' :
                news.sentiment === 'negative' ? 'bg-red-100 text-red-800' :
                'bg-gray-100 text-gray-800'
              }`}>
                {news.sentiment}
              </div>
            </div>
            
            <div className="flex items-center justify-between text-xs text-gray-500 mb-3">
              <div className="flex items-center gap-4">
                <span>{news.source}</span>
                <span>{new Date(news.timestamp).toLocaleDateString()}</span>
                <span>{new Date(news.timestamp).toLocaleTimeString()}</span>
              </div>
              <div className="flex items-center gap-2">
                <button className="p-1 hover:bg-gray-100 rounded">
                  <Share2 size={14} />
                </button>
                <button className="p-1 hover:bg-gray-100 rounded">
                  <ExternalLink size={14} />
                </button>
              </div>
            </div>
            
            {news.relatedStocks.length > 0 && (
              <div className="pt-3 border-t border-gray-100">
                <div className="text-xs text-gray-500 mb-2">Related Stocks:</div>
                <div className="flex gap-2">
                  {news.relatedStocks.map((symbol) => {
                    const stock = state.stocks.find(s => s.symbol === symbol);
                    return (
                      <div key={symbol} className="flex items-center gap-2 bg-gray-50 px-3 py-1 rounded">
                        <span className="font-medium text-sm">{symbol}</span>
                        {stock && (
                          <div className={`flex items-center gap-1 ${
                            stock.changePercent > 0 ? 'positive-change' : 
                            stock.changePercent < 0 ? 'negative-change' : 'neutral-change'
                          }`}>
                            {stock.changePercent > 0 ? <TrendingUp size={12} /> : <TrendingDown size={12} />}
                            <span className="text-xs">
                              {stock.changePercent > 0 ? '+' : ''}{stock.changePercent.toFixed(2)}%
                            </span>
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          </div>
        ))}
        
        {filteredNews.length === 0 && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">ðŸ“°</div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">No news found</h3>
            <p className="text-gray-600">Try selecting a different category</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default NewsScreen;
