import React from 'react';
import { useApp } from '../context/AppContext';
import BottomNavigation from '../components/BottomNavigation';

const NewsScreen: React.FC = () => {
  const { dispatch } = useApp();

  const newsItems = [
    {
      id: 1,
      title: 'Tech Stocks Rally on AI Breakthrough',
      summary: 'Major technology companies see significant gains following new AI developments.',
      source: 'Financial Times',
      time: '2 hours ago',
      category: 'Technology'
    },
    {
      id: 2,
      title: 'Federal Reserve Signals Rate Cut',
      summary: 'Central bank hints at potential interest rate reduction in next quarter.',
      source: 'Wall Street Journal',
      time: '4 hours ago',
      category: 'Economy'
    },
    {
      id: 3,
      title: 'Tesla Reports Record Q4 Earnings',
      summary: 'Electric vehicle manufacturer exceeds analyst expectations with strong performance.',
      source: 'Reuters',
      time: '6 hours ago',
      category: 'Automotive'
    },
    {
      id: 4,
      title: 'Oil Prices Surge on Supply Concerns',
      summary: 'Crude oil futures rise amid geopolitical tensions in key producing regions.',
      source: 'Bloomberg',
      time: '8 hours ago',
      category: 'Energy'
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
      {/* Header */}
      <div className="p-6 pt-12">
        <div className="flex justify-between items-center">
          <h1 className="text-2xl font-bold text-white">Market News</h1>
          <button
            onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'home' })}
            className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center text-white hover:bg-white/20 transition-colors"
          >
            ←
          </button>
        </div>
      </div>

      {/* News Feed */}
      <div className="px-6 mb-20">
        <div className="space-y-4">
          {newsItems.map((item) => (
            <div
              key={item.id}
              className="bg-white/10 backdrop-blur-sm rounded-xl p-6 border border-white/20 hover:border-cyan-400/50 transition-all duration-300 cursor-pointer"
            >
              <div className="flex justify-between items-start mb-3">
                <span className="px-3 py-1 bg-cyan-400/20 text-cyan-400 rounded-full text-xs font-semibold">
                  {item.category}
                </span>
                <span className="text-gray-400 text-xs">{item.time}</span>
              </div>
              <h3 className="text-white font-semibold text-lg mb-2">{item.title}</h3>
              <p className="text-gray-300 text-sm mb-3">{item.summary}</p>
              <div className="flex justify-between items-center">
                <span className="text-gray-400 text-xs">{item.source}</span>
                <button className="text-cyan-400 hover:text-cyan-300 text-sm font-semibold">
                  Read More →
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      <BottomNavigation />
    </div>
  );
};

export default NewsScreen;
