import React, { useState } from 'react';
import { useApp } from '../context/AppContext';
import { Stock } from '../types';

const StockDetailScreen: React.FC = () => {
  const { state, dispatch } = useApp();
  const { currentStock, watchlist } = state;
  const [showBuyModal, setShowBuyModal] = useState(false);
  const [quantity, setQuantity] = useState('');

  // Use mock stock data if no current stock
  const stock: Stock = currentStock || {
    symbol: 'AAPL',
    name: 'Apple Inc.',
    price: 175.50,
    change: 2.50,
    changePercent: 1.44,
    sector: 'Technology',
    marketCap: '2.8T',
    peRatio: 28.5,
    matchPercentage: 95,
    matchReasons: ['Strong fundamentals', 'Innovation leader'],
    volume: 50000000,
    high: 198.23,
    low: 124.17,
    open: 173.00,
    previousClose: 173.00,
    description: 'Apple Inc. designs, manufactures, and markets smartphones, personal computers, tablets, wearables, and accessories worldwide.',
    companyInfo: {
      founded: 1976,
      employees: 164000,
      headquarters: 'Cupertino, CA',
      website: 'www.apple.com',
      industry: 'Consumer Electronics'
    }
  };

  const isInWatchlist = watchlist.some(w => w.symbol === stock.symbol);

  const handleAddToWatchlist = () => {
    if (isInWatchlist) {
      dispatch({ type: 'REMOVE_FROM_WATCHLIST', payload: stock.symbol });
    } else {
      dispatch({ type: 'ADD_TO_WATCHLIST', payload: stock });
    }
  };

  const handleBuyStock = () => {
    if (!quantity || parseFloat(quantity) <= 0) return;

    const shares = parseFloat(quantity);
    const totalCost = shares * stock.price;

    // Create portfolio item
    const portfolioItem = {
      symbol: stock.symbol,
      shares: shares,
      averagePrice: stock.price,
      currentValue: totalCost,
      totalGain: 0,
      totalGainPercent: 0
    };

    dispatch({ type: 'ADD_TO_PORTFOLIO', payload: portfolioItem });
    setShowBuyModal(false);
    setQuantity('');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
      {/* Header */}
      <div className="p-6 pt-12">
        <div className="flex justify-between items-center">
          <button
            onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'home' })}
            className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center text-white hover:bg-white/20 transition-colors"
          >
            ←
          </button>
          <h1 className="text-2xl font-bold text-white">Stock Details</h1>
          <button 
            onClick={handleAddToWatchlist}
            className={`w-10 h-10 rounded-full flex items-center justify-center transition-colors ${
              isInWatchlist 
                ? 'bg-yellow-500/20 text-yellow-400' 
                : 'bg-white/10 text-white hover:bg-white/20'
            }`}
          >
            ⭐
          </button>
        </div>
      </div>

      {/* Stock Info */}
      <div className="px-6 mb-6">
        <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-6 border border-white/20">
          <div className="flex items-center space-x-4 mb-4">
            <div className="w-16 h-16 bg-gradient-to-r from-cyan-400 to-purple-500 rounded-xl flex items-center justify-center">
              <span className="text-white font-bold text-xl">{stock.symbol.slice(0, 1)}</span>
            </div>
            <div>
              <h2 className="text-2xl font-bold text-white">{stock.symbol}</h2>
              <p className="text-gray-400">{stock.name}</p>
            </div>
          </div>
          <div className="flex justify-between items-center">
            <div>
              <p className="text-3xl font-bold text-white">${stock.price.toFixed(2)}</p>
              <p className={`text-sm ${stock.changePercent >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                {stock.change >= 0 ? '+' : ''}${stock.change.toFixed(2)} ({stock.changePercent >= 0 ? '+' : ''}{stock.changePercent.toFixed(2)}%)
              </p>
            </div>
            <div className="text-right">
              <p className="text-sm text-gray-400">Market Cap</p>
              <p className="text-white font-semibold">{stock.marketCap}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Chart Placeholder */}
      <div className="px-6 mb-6">
        <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-6 border border-white/20">
          <h3 className="text-white font-semibold text-lg mb-4">Price Chart</h3>
          <div className="h-48 bg-gradient-to-r from-cyan-400/20 to-purple-500/20 rounded-lg flex items-center justify-center">
            <p className="text-gray-400">Chart coming soon...</p>
          </div>
        </div>
      </div>

      {/* Actions */}
      <div className="px-6 mb-6">
        <div className="grid grid-cols-2 gap-4">
          <button 
            onClick={() => setShowBuyModal(true)}
            className="bg-gradient-to-r from-cyan-400 to-purple-500 text-white font-semibold py-3 rounded-xl hover:shadow-lg hover:shadow-cyan-400/25 transition-all duration-300"
          >
            Buy
          </button>
          <button className="bg-white/10 text-white font-semibold py-3 rounded-xl border border-white/20 hover:bg-white/20 transition-all duration-300">
            Sell
          </button>
        </div>
      </div>

      {/* Company Info */}
      <div className="px-6 mb-6">
        <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-6 border border-white/20">
          <h3 className="text-white font-semibold text-lg mb-4">Company Overview</h3>
          <p className="text-gray-300 text-sm leading-relaxed">
            {stock.description}
          </p>
        </div>
      </div>

      {/* Key Stats */}
      <div className="px-6 mb-20">
        <h3 className="text-white font-semibold text-lg mb-4">Key Statistics</h3>
        <div className="grid grid-cols-2 gap-4">
          <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 border border-white/20">
            <p className="text-gray-400 text-sm">P/E Ratio</p>
            <p className="text-white font-semibold">{stock.peRatio}</p>
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 border border-white/20">
            <p className="text-gray-400 text-sm">Dividend Yield</p>
            <p className="text-white font-semibold">0.5%</p>
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 border border-white/20">
            <p className="text-gray-400 text-sm">52W High</p>
            <p className="text-white font-semibold">${stock.high.toFixed(2)}</p>
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 border border-white/20">
            <p className="text-gray-400 text-sm">52W Low</p>
            <p className="text-white font-semibold">${stock.low.toFixed(2)}</p>
          </div>
        </div>
      </div>

      {/* Buy Modal */}
      {showBuyModal && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
          <div className="bg-white/10 backdrop-blur-sm p-6 rounded-2xl border border-white/20 w-full max-w-md">
            <h3 className="text-xl font-bold text-white mb-4">Buy {stock.symbol}</h3>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">Number of Shares</label>
                <input
                  type="number"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                  placeholder="0"
                  className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:border-cyan-400 focus:outline-none"
                />
              </div>

              {quantity && parseFloat(quantity) > 0 && (
                <div className="bg-white/5 p-4 rounded-lg">
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-300">Price per share:</span>
                    <span className="text-white">${stock.price.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-300">Total cost:</span>
                    <span className="text-white font-semibold">${(parseFloat(quantity) * stock.price).toFixed(2)}</span>
                  </div>
                </div>
              )}
            </div>

            <div className="flex space-x-3 mt-6">
              <button
                onClick={() => setShowBuyModal(false)}
                className="flex-1 py-2 px-4 bg-white/10 hover:bg-white/20 text-white rounded-lg transition-colors"
              >
                Cancel
              </button>
              <button
                onClick={handleBuyStock}
                disabled={!quantity || parseFloat(quantity) <= 0}
                className="flex-1 py-2 px-4 bg-gradient-to-r from-cyan-400 to-purple-500 hover:from-cyan-500 hover:to-purple-600 text-white rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Buy
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StockDetailScreen;
