import React, { useState, useEffect } from 'react';
import { useApp } from '../context/AppContext';
import { mockStocks, mockNews, mockForumPosts } from '../data/mockData';

const HomeScreen: React.FC = () => {
  const { state } = useApp();
  const { user } = state;
  const [portfolioValue, setPortfolioValue] = useState(25000);
  const [dailyChange, setDailyChange] = useState(1250);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const getMatchGlow = (percentage: number) => {
    if (percentage >= 80) return 'neon-glow-green';
    if (percentage >= 60) return 'neon-glow-orange';
    return 'neon-glow-orange';
  };

  const topMatches = mockStocks.slice(0, 3);

  const handleAddToWatchlist = (stock: any) => {
    dispatch({ type: 'ADD_TO_WATCHLIST', payload: stock });
  };

  const handleViewStock = (stock: any) => {
    dispatch({ type: 'SET_CURRENT_STOCK', payload: stock });
    dispatch({ type: 'SET_SCREEN', payload: 'stock-detail' });
  };

  const handleViewForum = () => {
    dispatch({ type: 'SET_SCREEN', payload: 'forum' });
  };

  return (
    <div className={`min-h-screen bg-gradient-to-br from-dark-950 via-dark-900 to-dark-800 transition-all duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      <div className="p-4 pb-20">
        {/* Header */}
        <div className="mb-6">
          <h1 className="text-2xl font-bold gradient-text mb-2">
            {getGreeting()}, {user?.name || 'Investor'}!
          </h1>
          <p className="text-gray-300 text-sm">Here's what's happening in your investment universe</p>
        </div>

        {/* Top Matches Section */}
        <div className="mb-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-bold text-white">Top Matches</h2>
            <div className="text-xs text-gray-400">Based on your preferences</div>
          </div>

          <div className="space-y-3">
            {topMatches.map((stock, index) => (
              <div 
                key={stock.symbol}
                className={`card-glow p-4 animate-slide-up`}
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="relative">
                      <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl flex items-center justify-center text-lg font-bold text-white">
                        {stock.symbol.slice(0, 2)}
                      </div>
                      <div className={`absolute -top-1 -right-1 w-6 h-6 rounded-full bg-gradient-to-r from-green-500 to-blue-600 flex items-center justify-center text-xs font-bold text-white ${getMatchGlow(stock.matchPercentage)}`}>
                        {stock.matchPercentage}%
                      </div>
                    </div>

                    <div>
                      <h3 className="text-lg font-bold text-white">{stock.name}</h3>
                      <p className="text-gray-400 text-sm">{stock.symbol}</p>
                      <div className="flex items-center space-x-2 mt-1">
                        <span className={`text-base font-bold ${stock.changePercent >= 0 ? 'positive-change' : 'negative-change'}`}>
                          ${stock.price.toFixed(2)}
                        </span>
                        <div className={`flex items-center text-xs ${stock.changePercent >= 0 ? 'positive-change' : 'negative-change'}`}>
                          <span className="mr-1">{stock.changePercent >= 0 ? '‚Üó' : '‚Üò'}</span>
                          <span>{Math.abs(stock.changePercent).toFixed(2)}%</span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleAddToWatchlist(stock)}
                      className="p-2 bg-white/10 hover:bg-white/20 rounded-lg transition-colors duration-200"
                    >
                      <span className="text-blue-400 text-lg">+</span>
                    </button>
                    <button
                      onClick={() => handleViewStock(stock)}
                      className="p-2 bg-white/10 hover:bg-white/20 rounded-lg transition-colors duration-200"
                    >
                      <span className="text-purple-400 text-lg">üëÅ</span>
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Market Sentiment */}
        <div className="mb-6">
          <h2 className="text-xl font-bold text-white mb-4">Market Sentiment</h2>
          <div className="grid grid-cols-3 gap-3">
            <div className="card text-center p-3">
              <div className="text-xl mb-1">üìà</div>
              <div className="text-lg font-bold gradient-text-green">68%</div>
              <div className="text-xs text-gray-400">Bullish</div>
              <div className="progress-bar mt-2">
                <div className="progress-fill" style={{ width: '68%' }}></div>
              </div>
            </div>

            <div className="card text-center p-3">
              <div className="text-xl mb-1">üìä</div>
              <div className="text-lg font-bold text-gray-300">22%</div>
              <div className="text-xs text-gray-400">Neutral</div>
              <div className="progress-bar mt-2">
                <div className="progress-fill bg-gray-500" style={{ width: '22%' }}></div>
              </div>
            </div>

            <div className="card text-center p-3">
              <div className="text-xl mb-1">üìâ</div>
              <div className="text-lg font-bold gradient-text-orange">10%</div>
              <div className="text-xs text-gray-400">Bearish</div>
              <div className="progress-bar mt-2">
                <div className="progress-fill bg-orange-500" style={{ width: '10%' }}></div>
              </div>
            </div>
          </div>
        </div>

        {/* Latest News */}
        <div className="mb-6">
          <h2 className="text-xl font-bold text-white mb-4">Latest News</h2>
          <div className="space-y-3">
            {mockNews.slice(0, 2).map((article, index) => (
              <div 
                key={article.id}
                className="card-hover p-3 animate-slide-up"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-start space-x-3">
                  <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center text-white font-bold text-xs">
                    {article.source.slice(0, 2).toUpperCase()}
                  </div>
                  <div className="flex-1">
                    <h3 className="font-semibold text-white mb-1 line-clamp-2 text-sm">{article.title}</h3>
                    <div className="flex items-center justify-between text-xs text-gray-400">
                      <span>{article.source}</span>
                      <span>{new Date(article.timestamp).toLocaleDateString()}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Community Discussions */}
        <div className="mb-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-bold text-white">Community</h2>
            <button
              onClick={handleViewForum}
              className="text-blue-400 hover:text-blue-300 transition-colors text-sm"
            >
              View All
            </button>
          </div>

          <div className="space-y-3">
            {mockForumPosts.slice(0, 2).map((post, index) => (
              <div 
                key={post.id}
                className="card-hover p-3 animate-slide-up"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-start space-x-3">
                  <div className="w-8 h-8 bg-gradient-to-br from-purple-500 to-pink-600 rounded-full flex items-center justify-center text-white font-bold text-xs">
                    {post.author.name.slice(0, 1).toUpperCase()}
                  </div>
                  <div className="flex-1">
                    <h3 className="font-semibold text-white mb-1 line-clamp-1 text-sm">{post.title}</h3>
                    <p className="text-gray-400 text-xs mb-2 line-clamp-2">{post.content}</p>
                    <div className="flex items-center space-x-3 text-xs text-gray-500">
                      <span className="flex items-center space-x-1">
                        <span className="text-sm">üí¨</span>
                        <span>{post.comments?.length || 0}</span>
                      </span>
                      <span className="flex items-center space-x-1">
                        <span className="text-sm">‚≠ê</span>
                        <span>{post.upvotes - post.downvotes}</span>
                      </span>
                      <span className="badge-primary text-xs">{post.niche}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="grid grid-cols-2 gap-3">
          <button
            onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'portfolio' })}
            className="card-glow p-4 text-center hover:scale-105 transition-transform duration-200"
          >
            <div className="text-2xl mb-2">üíº</div>
            <h3 className="font-bold text-white mb-1 text-sm">Portfolio</h3>
            <p className="text-xs text-gray-400">View your investments</p>
          </button>

          <button
            onClick={() => dispatch({ type: 'SET_SCREEN', payload: 'news' })}
            className="card-glow p-4 text-center hover:scale-105 transition-transform duration-200"
          >
            <div className="text-2xl mb-2">üì∞</div>
            <h3 className="font-bold text-white mb-1 text-sm">News</h3>
            <p className="text-xs text-gray-400">Market updates</p>
          </button>
        </div>
      </div>
    </div>
  );
};

export default HomeScreen;
