import React, { useState } from 'react';
import { useApp } from '../context/AppContext';
import BottomNavigation from '../components/BottomNavigation';

const NewsScreen: React.FC = () => {
  const { state } = useApp();
  const [selectedCategory, setSelectedCategory] = useState('all');

  const categories = [
    { id: 'all', name: 'All News' },
    { id: 'market', name: 'Market Overview' },
    { id: 'earnings', name: 'Earnings' },
    { id: 'technology', name: 'Technology' },
    { id: 'breaking', name: 'Breaking News' },
    { id: 'analysis', name: 'Analysis & Opinion' }
  ];

  const filteredNews = state.news.filter(news => {
    if (selectedCategory === 'all') return true;
    return news.category.toLowerCase().includes(selectedCategory);
  });

  const marketIndices = [
    { symbol: 'S&P 500', value: '4,783.35', change: '+0.45%', positive: true },
    { symbol: 'NASDAQ', value: '15,003.22', change: '+0.75%', positive: true },
    { symbol: 'DOW', value: '37,592.98', change: '-0.12%', positive: false }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-purple-900 text-white">
      {/* Header */}
      <div className="p-6 pt-12">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold mb-2 bg-gradient-to-r from-cyan-400 to-blue-500 bg-clip-text text-transparent">
            Market News
          </h1>
          <p className="text-gray-400">Stay ahead of the market</p>
        </div>
        
        {/* Market Overview */}
        <div className="grid grid-cols-3 gap-4 mb-6">
          {marketIndices.map((index) => (
            <div key={index.symbol} className="card-hover p-4 text-center">
              <div className="text-sm text-gray-400 mb-1">{index.symbol}</div>
              <div className="font-bold text-white text-lg">{index.value}</div>
              <div className={`text-sm font-medium ${index.positive ? 'positive-change' : 'negative-change'}`}>
                {index.change}
              </div>
            </div>
          ))}
        </div>

        {/* Categories */}
        <div className="flex gap-2 overflow-x-auto pb-4 mb-6">
          {categories.map((category) => (
            <button
              key={category.id}
              onClick={() => setSelectedCategory(category.id)}
              className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap transition-all duration-300 ${
                selectedCategory === category.id
                  ? 'bg-gradient-to-r from-cyan-500 to-blue-600 text-white shadow-lg shadow-cyan-500/25'
                  : 'bg-white/10 text-gray-300 hover:bg-white/20 hover:text-white'
              }`}
            >
              {category.name}
            </button>
          ))}
        </div>
      </div>

      {/* News Feed */}
      <div className="px-6 space-y-4 mb-20">
        {filteredNews.map((news) => (
          <div key={news.id} className="card-hover p-6">
            <div className="flex justify-between items-start mb-4">
              <div className="flex-1">
                <h3 className="font-bold text-lg text-white mb-3 line-clamp-2">{news.title}</h3>
                <p className="text-gray-300 text-sm mb-4 line-clamp-3">{news.summary}</p>
              </div>
              <div className={`ml-4 px-3 py-1 rounded text-xs font-medium ${
                news.sentiment === 'positive' ? 'bg-green-500/20 text-green-400 border border-green-500/30' :
                news.sentiment === 'negative' ? 'bg-red-500/20 text-red-400 border border-red-500/30' :
                'bg-gray-500/20 text-gray-400 border border-gray-500/30'
              }`}>
                {news.sentiment}
              </div>
            </div>
            
            <div className="flex items-center justify-between text-xs text-gray-400 mb-4">
              <div className="flex items-center gap-4">
                <span>{news.source}</span>
                <span>{new Date(news.timestamp).toLocaleDateString()}</span>
                <span>{new Date(news.timestamp).toLocaleTimeString()}</span>
              </div>
              <div className="flex items-center gap-2">
                <button className="p-2 hover:bg-white/10 rounded-lg transition-colors">
                  <span className="text-lg">📤</span>
                </button>
                <button className="p-2 hover:bg-white/10 rounded-lg transition-colors">
                  <span className="text-lg">🔗</span>
                </button>
              </div>
            </div>
            
            {news.relatedStocks.length > 0 && (
              <div className="pt-4 border-t border-gray-700">
                <div className="text-xs text-gray-400 mb-3">Related Stocks:</div>
                <div className="flex gap-2 flex-wrap">
                  {news.relatedStocks.map((symbol) => {
                    const stock = state.stocks.find(s => s.symbol === symbol);
                    return (
                      <div key={symbol} className="flex items-center gap-2 bg-white/5 px-3 py-2 rounded-lg border border-white/10">
                        <span className="font-medium text-sm text-white">{symbol}</span>
                        {stock && (
                          <div className={`flex items-center gap-1 ${
                            stock.changePercent > 0 ? 'positive-change' : 
                            stock.changePercent < 0 ? 'negative-change' : 'text-gray-400'
                          }`}>
                            <span className="text-xs">
                              {stock.changePercent > 0 ? '↗' : stock.changePercent < 0 ? '↘' : '→'}
                            </span>
                            <span className="text-xs">
                              {stock.changePercent > 0 ? '+' : ''}{stock.changePercent.toFixed(2)}%
                            </span>
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          </div>
        ))}
        
        {filteredNews.length === 0 && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">📰</div>
            <h3 className="text-xl font-semibold text-white mb-2">No news found</h3>
            <p className="text-gray-400">Try selecting a different category</p>
          </div>
        )}
      </div>

      <BottomNavigation />
    </div>
  );
};

export default NewsScreen;
