import React from 'react';
import { useApp } from '../context/AppContext';

const WelcomeScreen: React.FC = () => {
  const { dispatch } = useApp();

  const handleGetStarted = () => {
    dispatch({ type: 'SET_SCREEN', payload: 'onboarding' });
  };

  const handleSignIn = () => {
    // For demo purposes, we'll skip to home
    dispatch({ type: 'SET_SCREEN', payload: 'home' });
  };

  return (
    <div className="min-h-screen gradient-bg flex flex-col items-center justify-center px-6 text-white">
      {/* Animated Logo */}
      <div className="mb-8 animate-bounce-gentle">
        <div className="text-8xl mb-4 animate-pulse-slow">ðŸ“ˆ</div>
        <h1 className="text-5xl font-bold text-center animate-fade-in">
          Quantix
        </h1>
      </div>

      {/* Tagline */}
      <div className="text-center mb-12 animate-slide-up">
        <h2 className="text-3xl font-semibold mb-4">
          Find Your Niche
        </h2>
        <p className="text-xl opacity-90">
          Discover investments that match your style
        </p>
      </div>

      {/* Get Started Button */}
      <button
        onClick={handleGetStarted}
        className="btn-primary text-lg px-12 py-4 mb-8 animate-fade-in"
        style={{ animationDelay: '0.5s' }}
      >
        Get Started
      </button>

      {/* Sign In Link */}
      <button
        onClick={handleSignIn}
        className="text-white opacity-80 hover:opacity-100 transition-opacity duration-200 underline"
        style={{ animationDelay: '0.8s' }}
      >
        Already have an account? Sign in
      </button>

      {/* Background Decoration */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-20 h-20 bg-white opacity-10 rounded-full animate-pulse"></div>
        <div className="absolute bottom-20 right-10 w-32 h-32 bg-white opacity-5 rounded-full animate-pulse" style={{ animationDelay: '1s' }}></div>
        <div className="absolute top-1/2 left-20 w-16 h-16 bg-white opacity-8 rounded-full animate-pulse" style={{ animationDelay: '2s' }}></div>
      </div>
    </div>
  );
};

export default WelcomeScreen;
