import React, { createContext, useContext, useReducer, ReactNode } from 'react';
import { AppState, AppAction } from '../types';
import { mockStocks, mockNews, mockForumPosts } from '../data/mockData';

const initialState: AppState = {
  currentScreen: 'welcome',
  onboardingStep: 0,
  user: null,
  stocks: mockStocks,
  news: mockNews,
  forumPosts: mockForumPosts,
  currentStock: null,
  isLoading: false,
  error: null,
};

function appReducer(state: AppState, action: AppAction): AppState {
  switch (action.type) {
    case 'SET_SCREEN':
      return { ...state, currentScreen: action.payload };
    
    case 'SET_ONBOARDING_STEP':
      return { ...state, onboardingStep: action.payload };
    
    case 'SET_USER':
      return { ...state, user: action.payload };
    
    case 'UPDATE_USER_PREFERENCES':
      if (!state.user) return state;
      return {
        ...state,
        user: {
          ...state.user,
          preferences: { ...state.user.preferences, ...action.payload }
        }
      };
    
    case 'SET_STOCKS':
      return { ...state, stocks: action.payload };
    
    case 'SET_NEWS':
      return { ...state, news: action.payload };
    
    case 'SET_FORUM_POSTS':
      return { ...state, forumPosts: action.payload };
    
    case 'SET_CURRENT_STOCK':
      return { ...state, currentStock: action.payload };
    
    case 'SET_LOADING':
      return { ...state, isLoading: action.payload };
    
    case 'SET_ERROR':
      return { ...state, error: action.payload };
    
    case 'ADD_TO_WATCHLIST':
      if (!state.user) return state;
      return {
        ...state,
        user: {
          ...state.user,
          watchlist: [...state.user.watchlist, action.payload]
        }
      };
    
    case 'REMOVE_FROM_WATCHLIST':
      if (!state.user) return state;
      return {
        ...state,
        user: {
          ...state.user,
          watchlist: state.user.watchlist.filter(symbol => symbol !== action.payload)
        }
      };
    
    case 'ADD_TO_PORTFOLIO':
      if (!state.user) return state;
      return {
        ...state,
        user: {
          ...state.user,
          portfolio: [...state.user.portfolio, action.payload]
        }
      };
    
    case 'UPDATE_PORTFOLIO':
      if (!state.user) return state;
      return {
        ...state,
        user: {
          ...state.user,
          portfolio: state.user.portfolio.map(item =>
            item.symbol === action.payload.symbol ? action.payload : item
          )
        }
      };
    
    default:
      return state;
  }
}

interface AppContextType {
  state: AppState;
  dispatch: React.Dispatch<AppAction>;
}

const AppContext = createContext<AppContextType | undefined>(undefined);

export function AppProvider({ children }: { children: ReactNode }) {
  const [state, dispatch] = useReducer(appReducer, initialState);

  return (
    <AppContext.Provider value={{ state, dispatch }}>
      {children}
    </AppContext.Provider>
  );
}

export function useApp() {
  const context = useContext(AppContext);
  if (context === undefined) {
    throw new Error('useApp must be used within an AppProvider');
  }
  return context;
}
